CREATE TABLE TITLE (TITLE_ID NUMBER(10) NOT NULL, TITLE VARCHAR2(60) NOT NULL, DESCRIPTION VARCHAR2(400) NOT NULL, RATING VARCHAR2(4), CATEGORY VARCHAR2(20), RELEASE_DATE DATE, CONSTRAINT title_ui_1 UNIQUE(TITLE_ID), CONSTRAINT rating_check_1 CHECK(RATING IN('G', 'PG', 'R', 'NC17', 'NR')), CONSTRAINT category_check_1 CHECK (CATEGORY IN('DRAMA', 'COMEDY', 'ACTION', 'CHILD', 'SCIFI', 'DOCUMENTARY')));
