CREATE TABLE RENTAL (BOOK_DATE DATE DEFAULT SYSDATE, MEMBER_ID NUMBER(10) NOT NULL, COPY_ID NUMBER(10) NOT NULL, ACT_RET_DATE DATE, EXP_RET_DATE DATE DEFAULT SYSDATE+2, TITLE_ID NUMBER(10) NOT NULL, CONSTRAINT all_ui_1 UNIQUE(MEMBER_ID, COPY_ID, TITLE_ID), CONSTRAINT mem_fk_1 FOREIGN KEY(MEMBER_ID) REFERENCES MEMBER (MEMBER_ID), CONSTRAINT copy_fk_2 FOREIGN KEY(COPY_ID, TITLE_ID) REFERENCES TITLE_COPY(COPY_ID, TITLE_ID));
